create database spiracle;

use spiracle;

DROP TABLE users;
DROP TABLE address;
DROP TABLE TEXT_STORE;

CREATE TABLE users (
    id int,
    name varchar(500),
    surname varchar(1000),
    dob date,
    credit_card varchar(32),
    cvv int
);

CREATE TABLE address (
    id int,
    address_1 varchar(500),
    address_2 varchar(500),
    address_3 varchar(500)
);

CREATE TABLE TEXT_STORE
(
   ID    int,
   DATA  text
);


